import React from "react";
import "./CountryBadge.css";

// export default CountryBadge;
function capitalizeFirstLetter(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
}
export default function CountryBadge({ country }) {
  const url = `/events/countries/${country}`;
  const uppercaseCountry = capitalizeFirstLetter(country);
  return (
    <a href={url}>
      <span className="badge country-badge m-1">{uppercaseCountry}</span>
    </a>
  );
}
