import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

// IMplemented as a funcitonal react component
// Differs from class due to lack of state and lifecycle methods
// If all you need is accept props & return JSX, use functional components
// They won't always need their own file

{
  /* <th>User</th>
<th>Text</th>
<th>Interactions Per Minute</th>
<th>Total Interactions</th>
<th>Countries Mentioned</th> */
}

const Exercise = (props) => (
  <tr>
    <td>{props.topic._id}</td>
    <td>{props.topic.country_mentions.flat().join(", ")}</td>
    <td>{props.topic.ipm.toFixed(1)}</td>
  </tr>
);

// IMplement as a class component
export default class ExercisesList extends Component {
  constructor(props) {
    super(props);

    this.state = { topics: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/events/topics")
      .then((response) => {
        // console.log(response);
        this.setState({ topics: response.data });
        console.log(this.state);
      })
      .catch((error) => console.log(error));
  }

  exerciseList() {
    return this.state.topics.map((currentevent) => {
      return <Exercise topic={currentevent} key={currentevent._id} />;
    });
  }

  render() {
    return (
      <div>
        <h3>Global Heatmap</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Topics</th>
              <th>Places Mentioned</th>
              <th>Interactions Per Minute</th>
            </tr>
          </thead>
          <tbody>{this.exerciseList()}</tbody>
        </table>
      </div>
    );
  }
}
