import React, { Component } from "react";
import TopicBadge from "./TopicBadge";

// function Topics() {
//   return this.state.events.map((event) => <CardTopics topic={event.topics} />);
// }

export default class EventCard extends Component {
  constructor(props) {
    super(props);
    console.log(props);
    this.state = { topics: [] };
  }

  getTopics() {
    return this.props.event.topics.map((topic) => <TopicBadge topic={topic} />);
  }

  getLocations() {
    return this.props.event.country_mentions.map((country) => `${country}, `);
  }

  render() {
    return (
      <div className="card shadow m-3">
        <div className="card-body">
          <h4 className="card-title headline">{this.props.event.text}</h4>
          <h6 className="text-muted card-subtitle">
            {this.props.event.country_mentions}
          </h6>
          <span className="badge badge-primary topic-badge">
            {this.getTopics()}
          </span>
        </div>
      </div>
    );
  }
}

