import React, { Component } from "react";
import axios from "axios";

// import Topic
{
  /* <div className="card shadow mb-4">
<div className="card-body">{country}</div> */
}

export default class CountryProfile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      topics: [],
      capital: "",
      languages: [],
      flag: "",
    };
  }

  componentDidMount() {
    // console.log(this.props.country);
    fetch(`https://restcountries.eu/rest/v2/name/${this.props.country}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        const languages = data[0].languages.map((el) => el.name);
        this.setState({
          capital: data[0].capital,
          languages: languages.join(", "),
          flag: data[0].flag,
        });
      })
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="card shadow mb-4">
        <div className="row card-body">
          <div className="row">
            <div class="col-sm-6">
              <h5 class="card-title">...</h5>
              <p class="card-text">....</p>
              <a href="#" class="btn btn-primary">
                Go somewhere
              </a>
            </div>
            <img class="col-sm-6" src="Images/template.png" alt="sans" />
          </div>
          <img src={this.state.flag} className="card-img" height="100px" />
          <strong>Country:</strong> {this.props.country}
          <br />
          <strong>Capital:</strong> {this.state.capital}
          <br />
          <strong>Languages:</strong> {this.state.languages}
        </div>
      </div>
    );
  }
}
