// IMplement as a class component
export default class GlobalHeatmap extends Component {
  constructor(props) {
    super(props);

    this.state = { topics: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/events/topics")
      .then((response) => {
        // console.log(response);
        this.setState({ topics: response.data });
        console.log(this.state);
      })
      .catch((error) => console.log(error));
  }

  exerciseList() {
    return this.state.topics.map((currentevent) => {
      return <Exercise topic={currentevent} key={currentevent._id} />;
    });
  }

  render() {
    // var mymap = L.map("mapid").setView([51.505, -0.09], 13);

    return (
      <div>
        <h3>Global Heatmap</h3>
        <div id="mapid">
          <MapContainer center={[20, 20]} zoom={2} scrollWheelZoom={false}>
            <TileLayer
              attribution='&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>, &copy; <a href="https://openmaptiles.org/">OpenMapTiles</a>'
              url="https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png"
            />
            <Marker position={[51.505, -0.09]}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </MapContainer>
        </div>
      </div>
    );
  }
}
