import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import CountryBadge from "./countryBadge";

const Topic = (props) => (
  <tr>
    <td>{props.topic._id}</td>
    {/* <td>{props.topic.country_mentions.flat().join(", ")}</td> */}
    <td>{props.topic.country_mentions.map((topic) => topic)}</td>
    {/* <td>
      {props.topic.country_mentions.map((topic) => (
        <CountryBadge topic={topic} />
      ))}
    </td> */}
    <td>{props.topic.ipm.toFixed(1)}</td>
  </tr>
);

// IMplement as a class component
export default class TrendingTopics extends Component {
  constructor(props) {
    super(props);

    this.state = { topics: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/events/topics")
      .then((response) => {
        // console.log(response);
        this.setState({ topics: response.data });
        console.log(this.state);
      })
      .catch((error) => console.log(error));
  }

  exerciseList() {
    return this.state.topics.map((currentevent) => {
      return <Topic topic={currentevent} key={currentevent._id} />;
    });
  }

  render() {
    return (
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th>Topics</th>
            <th>Places Mentioned</th>
            <th>Interactions Per Minute</th>
          </tr>
        </thead>
        <tbody>{this.exerciseList()}</tbody>
      </table>
    );
  }
}
