export default class GlobalHeatmap extends Component {
  constructor(props) {
    super(props);

    this.state = {
      mentions: [],
      maplayers: [],
    };
  }

  getAllMapLayers() {
    const mentions = this.state.mentions;
    for (const m of mentions) {
      axios
        .get(`http://localhost:5000/map/mapjson/${m._id}`)
        .then((response) => this.setState({ maplayers: response.data }));
    }
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/events/mentions")
      .then((response) => {
        this.setState({ mentions: response.data });
      })
