import React from "react";
import "./CountryBadge.css";

const { features } = require("./../../data/countries.json");

function findCountryCode(country) {
  for (const f of features) {
    if (f.properties.ADMIN === country) {
      return f.properties.ISO_A3;
    }
  }
}
// const test = findCountryCode("United Kingdom");
// console.log(test);

export default function CountryBadge({ country }) {
  // Look up the country code from here and append to new URL
  // const url = `/events/countries/${country}`;
  const code = findCountryCode(country);
  const url = `/events/countries/code/${code}`;
  return (
    <a href={url}>
      <span className="badge country-badge m-1">{country}</span>
    </a>
  );
}
