import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

// import React from "react";
// import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
// import { Icon } from "leaflet";
// import * as parkData from "./data/skateboard-parks.json";

// import "./app.css";

export default class GlobalHeatmap extends Component {
  constructor(props) {
    super(props);

    this.state = {
      mentions: [],
      maplayers: [],
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:5000/events/mentions")
      .then((response) => {
        // console.log(response);
        this.setState({ mentions: response.data });
        return response.data;
      })
      .then((mentions) => {
        const testList = [];
        mentions.data.forEach((el) => {
          if (typeof el._id === "string") testList.push(el._id);
          return testList;
        });
      })
      .then((ids) => console.log(ids))
      .catch((error) => console.log(error));

    console.log(this.state);
    // axios
    //   .get("http://localhost:5000/map/maplayers")
    //   .then((response) => {
    //     // console.log(response);
    //     this.setState({ maplayers: response.data });
    //     console.log(this.state);
    //   })
    //   .catch((error) => console.log(error));
  }

  // getMapLayers() {
  //   axios
  //     .get("http://localhost:5000/map/maplayers")
  //     .then((response) => {
  //       // console.log(response);
  //       this.setState({ maplayers: response.data });
  //       console.log(this.state);
  //     })
  //     .catch((error) => console.log(error));
  // }

  render() {
    return (
      <div>
        <h3>Global Heatmap</h3>
        <div id="mapid">
          <MapContainer
            center={{ lat: 51.505, lng: -0.09 }}
            zoom={13}
            scrollWheelZoom={false}
          >
            <TileLayer
              attribution='&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>, &copy; <a href="https://openmaptiles.org/">OpenMapTiles</a> &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors'
              url="https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png"
            />
          </MapContainer>
        </div>
      </div>
    );
  }
}

