import React, { Component } from "react";

export default class CountryProfile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      topics: [],
      capital: "",
      languages: [],
      domain: "",
      organizations: [],
      flag: "",
    };
  }

  unpackObjects(arr) {
    return arr.map((el) => el.name);
  }

  componentDidMount() {
    // console.log(this.props.country);
    fetch(`https://restcountries.eu/rest/v2/name/${this.props.country}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        const languages = data[0].languages.map((el) => el.name);
        const organizations = data[0].regionalBlocs.map((el) => el.name);
        // const organizations
        this.setState({
          capital: data[0].capital,
          languages: languages.join(", "),
          flag: data[0].flag,
          domain: data[0].topLevelDomain,
          organizations: organizations.join(", "),
        });
      })
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="card shadow mb-4">
        <div className="row card-body">
          <div className="row">
            <div class="col-sm-6">
              <p class="card-text p-1">
                <strong>Capital:</strong> {this.state.capital}
                <br />
                <strong>Languages:</strong> {this.state.languages}
                <br />
                <strong>Website Domain:</strong> {this.state.domain}
                <br />
                <strong>Organizations:</strong> {this.state.organizations}
              </p>
            </div>
          </div>
          {/* <img src={this.state.flag} className="card-img" height="100px" /> */}
        </div>
      </div>
    );
  }
}
