import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

// const Map = (props) => (
//   <tr>
//     <td>{props.topic._id}</td>
//     <td>{props.topic.country_mentions.flat().join(", ")}</td>
//     <td>{props.topic.ipm.toFixed(1)}</td>
//   </tr>
// );

// IMplement as a class component
export default class GlobalHeatmap extends Component {
  constructor(props) {
    super(props);

    this.state = { mentions: [] };
    console.log(this.state);
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/events/mentions")
      .then((response) => {
        // console.log(response);
        this.setState({ mentions: response.data });
        console.log(this.state);
      })
      .catch((error) => console.log(error));
  }
  LocationMarker() {
    const [position, setPosition] = useState(null);
    const map = useMapEvents({
      click() {
        map.locate();
      },
      locationfound(e) {
        setPosition(e.latlng);
        map.flyTo(e.latlng, map.getZoom());
      },
    });

    return position === null ? null : (
      <Marker position={position}>
        <Popup>You are here</Popup>
      </Marker>
    );
  }

  render() {
    return (
      <div>
        <h3>Global Heatmap</h3>
        <div id="mapid">
          <MapContainer
            center={{ lat: 51.505, lng: -0.09 }}
            zoom={13}
            scrollWheelZoom={false}
          >
            <TileLayer
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <LocationMarker />
          </MapContainer>
          , )
        </div>
      </div>
    );
  }
}
