import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

// const Map = (props) => (
//   <tr>
//     <td>{props.topic._id}</td>
//     <td>{props.topic.country_mentions.flat().join(", ")}</td>
//     <td>{props.topic.ipm.toFixed(1)}</td>
//   </tr>
// );

// IMplement as a class component
export default class GlobalHeatmap extends Component {
  constructor(props) {
    super(props);

    this.state = { mentions: [] };
    console.log(this.state);
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/events/mentions")
      .then((response) => {
        // console.log(response);
        this.setState({ mentions: response.data });
        console.log(this.state);
      })
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div>
        <h3>Global Heatmap</h3>
        <div id="mapid">
          <MapContainer center={[20, 20]} zoom={2} scrollWheelZoom={false}>
            <TileLayer
              attribution='&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>, &copy; <a href="https://openmaptiles.org/">OpenMapTiles</a>'
              url="https://tiles.stadiamaps.com/tiles/alidade_smooth_dark/{z}/{x}/{y}{r}.png"
            />
            <Marker position={[51.505, -0.09]}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </MapContainer>
        </div>
      </div>
    );
  }
}
